import React, { useState, useRef, useEffect } from 'react';
import { useStore } from '../hooks/useStore';
import { Edit3, Save, Eye } from 'lucide-react';

interface TwoPanelEditorProps {
  pyhContent: string;   // text output from pyh_ast_to_output.py
  pyContent: string;    // actual Python code
}

const TwoPanelEditor: React.FC<TwoPanelEditorProps> = ({ pyhContent, pyContent }) => {
  const { repository, updatePyhContent, saveUserPhy, isLoading, editor } = useStore();
  const [leftWidth, setLeftWidth] = useState(50); // percentage
  const [isDragging, setIsDragging] = useState(false);
  const [hoveredLine, setHoveredLine] = useState<number | null>(null);
  const [activeLine, setActiveLine] = useState<number | null>(null);
  const [isEditing, setIsEditing] = useState(false);
  const [hasUnsavedChanges, setHasUnsavedChanges] = useState(false);
  const containerRef = useRef<HTMLDivElement>(null);


  // Split content into lines
  const pyhLines = pyhContent.split('\n');
  const pyLines = pyContent.split('\n');

  // Create line mappings for proper alignment
  const createAlignedLineNumbers = () => {
    const lineMappings = editor.lineMappings || [];
    const maxLines = Math.max(pyhLines.length, pyLines.length);
    const alignedLines = [];

    // Create a map of PHY line to Python line
    const pyhToPyMap = new Map<number, number>();
    const pyToPyhMap = new Map<number, number>();

    lineMappings.forEach(mapping => {
      pyhToPyMap.set(mapping.pyhLine, mapping.pyLine);
      pyToPyhMap.set(mapping.pyLine, mapping.pyhLine);
    });

    // Generate aligned line numbers - ensure we show all Python lines
    for (let i = 0; i < pyLines.length; i++) {
      const pyLineNum = i + 1;
      const pyhLineNum = i + 1;

      // Check if we have a mapping for this line
      const mappedPyhLine = pyToPyhMap.get(pyLineNum);

      alignedLines.push({
        pyhLine: mappedPyhLine || pyhLineNum,
        pyLine: pyLineNum,
        isMapped: !!mappedPyhLine
      });
    }

    return alignedLines;
  };

  const alignedLines = createAlignedLineNumbers();

  // Handle mouse down on divider
  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
  };

  // Handle mouse move for resizing
  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      if (!isDragging || !containerRef.current) return;

      const containerRect = containerRef.current.getBoundingClientRect();
      const newLeftWidth = ((e.clientX - containerRect.left) / containerRect.width) * 100;

      // Constrain between 20% and 80%
      const constrainedWidth = Math.min(Math.max(newLeftWidth, 20), 80);
      setLeftWidth(constrainedWidth);
    };

    const handleMouseUp = () => {
      setIsDragging(false);
    };

    if (isDragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging]);

  // Handle line hover for highlighting
  const handleLineHover = (lineNumber: number, panel: 'pyh' | 'py') => {
    setHoveredLine(lineNumber);

    // Find the corresponding line in the other panel
    const lineMappings = editor.lineMappings || [];
    if (panel === 'pyh') {
      // Find the Python line that maps to this PHY line
      const mapping = lineMappings.find(m => m.pyhLine === lineNumber);
      if (mapping) {
        setHoveredLine(mapping.pyLine);
      }
    } else {
      // Find the PHY line that maps to this Python line
      const mapping = lineMappings.find(m => m.pyLine === lineNumber);
      if (mapping) {
        setHoveredLine(mapping.pyhLine);
      }
    }
  };

  const handleLineLeave = () => {
    setHoveredLine(null);
  };

  // Handle textarea change for editable content
  const handlePyhChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    const newContent = e.target.value;
    updatePyhContent(newContent);
    setHasUnsavedChanges(true);
  };

  // Handle save button
  const handleSave = async () => {
    if (repository.selectedFile) {
      await saveUserPhy(repository.selectedFile, pyhContent);
      setHasUnsavedChanges(false);
    }
  };

  // Handle edit toggle
  const handleEditToggle = () => {
    setIsEditing(!isEditing);
  };

  // Handle textarea focus for active line highlighting
  const handleTextareaFocus = (e: React.FocusEvent<HTMLTextAreaElement>) => {
    const textarea = e.target;
    const cursorPosition = textarea.selectionStart;
    const textBeforeCursor = textarea.value.substring(0, cursorPosition);
    const lineNumber = textBeforeCursor.split('\n').length;
    setActiveLine(lineNumber);
  };

  const handleTextareaBlur = () => {
    setActiveLine(null);
  };

  // Show loading state
  if (isLoading) {
    return (
      <div className="flex h-full w-full bg-gray-50 items-center justify-center">
        <div className="text-center text-gray-500">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <div className="text-lg font-medium mb-2">Loading file content...</div>
          <div className="text-sm">Please wait while we load the file</div>
        </div>
      </div>
    );
  }

  // Show placeholder if no content
  if (!pyhContent && !pyContent) {
    return (
      <div className="flex h-full w-full bg-gray-50 items-center justify-center">
        <div className="text-center text-gray-500">
          <div className="text-lg font-medium mb-2">No file selected</div>
          <div className="text-sm">Select a Python file from the file explorer to view its content</div>
        </div>
      </div>
    );
  }

  return (
    <div 
      ref={containerRef}
      className="flex h-full w-full bg-gray-50"
    >
      {/* Left Panel - PYH Content (Editable) */}
      <div 
        className="bg-gray-100 border-r border-gray-300 font-mono text-sm relative flex flex-col"
        style={{ width: `${leftWidth}%` }}
      >
        {/* Header with Edit Toggle and Save Button */}
        <div className="flex items-center justify-between p-2 bg-gray-200 border-b border-gray-300">
          <div className="flex items-center space-x-2">
            <span className="text-sm font-medium text-gray-700">PHY Content</span>
            {hasUnsavedChanges && (
              <span className="text-xs text-orange-600">â€¢ Unsaved changes</span>
            )}
          </div>
          <div className="flex items-center space-x-2">
            <button
              onClick={handleEditToggle}
              className={`flex items-center space-x-1 px-2 py-1 rounded text-xs font-medium transition-colors ${
                isEditing 
                  ? 'bg-blue-600 text-white hover:bg-blue-700' 
                  : 'bg-gray-500 text-white hover:bg-gray-600'
              }`}
            >
              {isEditing ? <Eye className="h-3 w-3" /> : <Edit3 className="h-3 w-3" />}
              <span>{isEditing ? 'View' : 'Edit'}</span>
            </button>
            {isEditing && (
              <button
                onClick={handleSave}
                disabled={!hasUnsavedChanges}
                className="flex items-center space-x-1 px-2 py-1 rounded text-xs font-medium transition-colors disabled:opacity-50 disabled:cursor-not-allowed bg-green-600 text-white hover:bg-green-700"
              >
                <Save className="h-3 w-3" />
                <span>Save</span>
              </button>
            )}
          </div>
        </div>

        {/* Content Area */}
        <div className="flex-1 relative">
          {/* Line Numbers */}
          <div className="absolute left-0 top-0 w-8 bg-gray-200 border-r border-gray-300 h-full overflow-hidden">
            {alignedLines.map((lineInfo, index) => (
              <div
                key={index}
                className={`text-xs text-gray-500 flex items-center justify-center ${
                  hoveredLine === lineInfo.pyhLine ? 'bg-blue-100' : ''
                } ${activeLine === lineInfo.pyhLine ? 'bg-blue-200' : ''} ${
                  lineInfo.isMapped ? 'font-semibold text-blue-600' : ''
                }`}
                style={{ height: '20px', lineHeight: '20px' }}
                title={lineInfo.isMapped ? `Maps to Python line ${lineInfo.pyLine}` : ''}
              >
                {lineInfo.pyhLine}
              </div>
            ))}
          </div>

          {/* Content */}
          <div className="pl-10 pr-2 py-2 h-full">
            {isEditing ? (
              <textarea
                value={pyhContent}
                onChange={handlePyhChange}
                onFocus={handleTextareaFocus}
                onBlur={handleTextareaBlur}
                className="w-full h-full resize-none border-none outline-none bg-transparent text-gray-800 min-h-0"
                placeholder="Edit PHY content here..."
                spellCheck={false}
                style={{ 
                  height: 'calc(100vh - 250px)',
                  lineHeight: '20px',
                  fontSize: '14px'
                }}
              />
            ) : (
              <div 
                className="w-full h-full overflow-auto text-gray-800 font-mono"
                style={{ 
                  height: 'calc(100vh - 250px)',
                  lineHeight: '20px',
                  fontSize: '14px',
                  whiteSpace: 'pre-wrap'
                }}
              >
                {alignedLines.map((lineInfo, index) => {
                  const line = pyhLines[lineInfo.pyhLine - 1] || '';
                  return (
                    <div
                      key={index}
                      className={`${
                        hoveredLine === lineInfo.pyhLine ? 'bg-blue-50' : ''
                      } ${activeLine === lineInfo.pyhLine ? 'bg-blue-100' : ''} ${
                        lineInfo.isMapped ? 'border-l-2 border-blue-400 pl-1' : ''
                      }`}
                      style={{ 
                        minHeight: '20px', 
                        lineHeight: '20px',
                        whiteSpace: 'pre-wrap',
                        wordBreak: 'break-all'
                      }}
                      onMouseEnter={() => handleLineHover(lineInfo.pyhLine, 'pyh')}
                      onMouseLeave={handleLineLeave}
                      onClick={() => setActiveLine(lineInfo.pyhLine)}
                    >
                      {line || '\u00A0'}
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Resizable Divider */}
      <div
        className={`w-1 bg-gray-300 cursor-col-resize hover:bg-gray-400 transition-colors ${
          isDragging ? 'bg-gray-500' : ''
        }`}
        onMouseDown={handleMouseDown}
      >
        <div className="w-full h-full flex items-center justify-center">
          <div className="w-0.5 h-8 bg-gray-400 rounded"></div>
        </div>
      </div>

      {/* Right Panel - PY Content (Read-only) */}
      <div 
        className="bg-white font-mono text-sm relative flex flex-col"
        style={{ width: `${100 - leftWidth}%` }}
      >
        {/* Header with Edit Toggle and Save Button */}
        <div className="flex items-center justify-between p-2 bg-gray-200 border-b border-gray-300">
          <div className="flex items-center space-x-2">
            <span className="text-sm font-medium text-gray-700">Python Code</span>
          </div>
          <div className="flex items-center space-x-2">
            {/* Empty space to match PHY header height */}
            <div className="h-6"></div>
          </div>
        </div>

        {/* Content Area */}
        <div className="flex-1 relative">
          {/* Line Numbers */}
          <div className="absolute left-0 top-0 w-8 bg-gray-100 border-r border-gray-300 h-full overflow-hidden">
          {alignedLines.map((lineInfo, index) => (
            <div
              key={index}
              className={`h-5 text-xs text-gray-500 flex items-center justify-center ${
                hoveredLine === lineInfo.pyLine ? 'bg-blue-100' : ''
              } ${lineInfo.isMapped ? 'font-semibold text-blue-600' : ''}`}
              style={{ height: '20px', lineHeight: '20px' }}
              title={lineInfo.isMapped ? `Maps to PHY line ${lineInfo.pyhLine}` : ''}
            >
              {lineInfo.pyLine}
            </div>
          ))}
        </div>

          {/* Content */}
          <div className="pl-10 pr-2 h-full">
            <div 
              className="w-full h-full overflow-auto font-mono text-gray-800" 
              style={{ 
                height: 'calc(100vh - 250px)',
                lineHeight: '20px',
                fontSize: '14px',
                whiteSpace: 'pre-wrap',
                paddingTop: '0px'
              }}
            >
              {alignedLines.map((lineInfo, index) => {
                const line = pyLines[lineInfo.pyLine - 1] || '';
                return (
                  <div
                    key={index}
                    className={`${
                      hoveredLine === lineInfo.pyLine ? 'bg-blue-50' : ''
                    } ${lineInfo.isMapped ? 'border-l-2 border-blue-400 pl-1' : ''}`}
                    style={{ 
                      minHeight: '20px', 
                      lineHeight: '20px',
                      whiteSpace: 'pre-wrap',
                      wordBreak: 'break-all'
                    }}
                    onMouseEnter={() => handleLineHover(lineInfo.pyLine, 'py')}
                    onMouseLeave={handleLineLeave}
                  >
                    {line || '\u00A0'} {/* Non-breaking space for empty lines */}
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default TwoPanelEditor;
